Command("create portal","","Creates a new portal.",  function(command,player,message)    if message ~= "" then      Message("&eSyntax: "..command.syntax):Send(player)	  return    end    playerNextBlock[player] = { handle = player.BlockEvent:Add(PortalCreateFirst), name = "Portal creation" }	Message("&ePlace the first portal."):Send(player)  end)function PortalCreateFirst(player,args)  Message("&ePlace the second portal."):Send(player)  player.BlockEvent:Remove(playerNextBlock[player].handle)  playerNextBlock[player] = { handle = player.BlockEvent:Add(PortalCreateSecond), name = "Portal creation", first = args }  args.Abort = trueendfunction PortalCreateSecond(player,second)  local first = playerNextBlock[player].first  player.BlockEvent:Remove(playerNextBlock[player].handle)  playerNextBlock[player] = nil  Message("&ePortal created: "..first.X..","..first.Y..","..first.Z..          " <=> "..second.X..","..second.Y..","..second.Z.."."):Send(player)  table.insert(levelPortals,Portal(player.Level,first,second))  if second.Type ~= 0 then second.Abort = true endend