Command("create portal","","Creates a new portal.",  function(command,player,message)    if message ~= "" then      Message("&eSyntax: "..command.syntax):Send(player)	  return    end    playerNextBlock[player] = { handle = player.BlockEvent:Add(PortalCreateFirst), name = "Portal creation" }	Message("&ePlace the first portal."):Send(player)  end)function PortalCreateFirst(player,args)  Message("&ePlace the second portal."):Send(player)  player.BlockEvent:Remove(playerNextBlock[player].handle)  playerNextBlock[player] = { handle = player.BlockEvent:Add(PortalCreateSecond), name = "Portal creation", first = args }  args.Abort = trueendfunction PortalCreateSecond(player,second)  local first = playerNextBlock[player].first  player.BlockEvent:Remove(playerNextBlock[player].handle)  playerNextBlock[player] = nil  Message("&ePortal created: "..first.X..","..first.Y..","..first.Z..          " <=> "..second.X..","..second.Y..","..second.Z.."."):Send(player)  player.Level:Cuboid(nil,first.X,first.Y,first.Z,first.X+1,first.Y+2,first.Z+1,0)  player.Level:Cuboid(nil,second.X,second.Y,second.Z,second.X+1,second.Y+2,second.Z+1,0)  local portalEntrance = Portal(player.Level,first.X,first.Y,first.Z,first.X+1,first.Y+2,first.Z+1);  local portalExit = Portal(player.Level,second.X,second.Y,second.Z,second.X+1,second.Y+2,second.Z+1);  portalEntrance:Connect(portalExit)  if second.Type ~= 0 then second.Abort = true endend