Command("destroy","","Destroys stuff.",  function(command,player,message)    if message ~= "" then      Message("&eSyntax: "..command.syntax):Send(player)	  return    end    playerNextBlock[player] = { handle = player.BlockEvent:Add(DestroySomething), name = "Portal button creation" }	Message("&eSelect something."):Send(player)  end)function DestroySomething(player,args)  player.BlockEvent:Remove(playerNextBlock[player].handle)  local regions = player.Level:RegionsAt(args.X,args.Y,args.Z)  args.Abort = true  if regions.Count == 0 then    Message("&eThere is nothing at this position."):Send(player)    return  end  for i = 0,regions.Count-1 do    local region = regions[i]    local name = region:GetType():ToString()    name = name:split(".")    name = name[#name]    Message("&e"..name.." destroyed."):Send(player)    if name == "Portal" then      region.Exit:Destroy()    end    region:Destroy()  endend