Command("hide","<name>","Hides a body/player.",  function(command,player,message)    if message == "" then      Message("&eSyntax: "..command.syntax):Send(player)	  return    end    message = message:lower()    local i,body = 0,nil    while i<player.Level.Bodies.Count do      body = player.Level.Bodies[i]      if body.Name:lower() == message then        body.Visible = false      end      i = i + 1    end  end)